--Q1: Find the team with the highest number of wins in 2024 and the total matches it won.

SELECT TOP 1 Winner, COUNT(*) AS 'Number of Wins'
FROM T20I
WHERE YEAR(MatchDate) = 2024
GROUP BY Winner
ORDER BY 'Number of Wins' DESC;.

--Q2: Rank the teams based on the total number of wins in 2024.

SELECT Winner, COUNT(*) AS 'Number of Wins', 
       ROW_NUMBER() OVER(ORDER BY COUNT(*) DESC) AS Rank_Assigned
FROM T20I
WHERE YEAR(MatchDate) = 2024
GROUP BY Winner;


--Q3: Which team had the highest average winning margin (in runs), and what was the average margin?

SELECT TOP 1 Winner,
       AVG(CAST(SUBSTRING(Margin, 1, CHARINDEX(' ', Margin) - 1) AS INT)) AS Avg_Margin
FROM T20I
WHERE Margin LIKE '%runs%'
GROUP BY Winner
ORDER BY Avg_Margin DESC;

--Q4: Which team had the highest average winning margin (in runs), and what was the average margin?

SELECT TOP 1 Winner,
       AVG(CAST(SUBSTRING(Margin, 1, CHARINDEX(' ', Margin) - 1) AS INT)) AS Avg_Margin
FROM T20I
WHERE Margin LIKE '%runs%'
GROUP BY Winner
ORDER BY Avg_Margin DESC;

--Q5: Identify matches played between two specific teams (e.g., India and South Africa) in 2024 and their results.

SELECT *
FROM T20I
WHERE (Team1 = 'India' AND Team2 = 'South Africa') 
   OR (Team1 = 'South Africa' AND Team2 = 'India')
   AND YEAR(MatchDate) = 2024;

--Q6: List all matches where the winning margin was greater than the average margin across all matches.

WITH AvgMargin AS (
    SELECT AVG(CAST(SUBSTRING(Margin, 1, CHARINDEX(' ', Margin) - 1) AS INT)) AS Avg_Margin
    FROM T20I
    WHERE Margin LIKE '%runs%'
)
SELECT * 
FROM T20I
LEFT JOIN AvgMargin A ON 1=1
WHERE Margin LIKE '%runs%'
  AND CAST(SUBSTRING(Margin, 1, CHARINDEX(' ', Margin) - 1) AS INT) > A.Avg_Margin;















